+namespace spec\DevBoardLib\GithubApiFacade\User;
+
+use DevBoardLib\GithubApiFacade\Auth\GithubAccessToken;
+use DevBoardLib\GithubApiFacade\Client\ClientFactory;
+use Github\Client;
+use PhpSpec\ObjectBehavior;
+use Prophecy\Argument;
+
+class UserFacadeFactorySpec extends ObjectBehavior
+{
+    public function let(ClientFactory $clientFactory)
+    {
+        $this->beConstructedWith($clientFactory);
+    }
+
+    public function it_is_initializable()
+    {
+        $this->shouldHaveType('DevBoardLib\GithubApiFacade\User\UserFacadeFactory');
+    }
+
+    public function it_will_create_repo_facade($clientFactory, GithubAccessToken $user, Client $client)
+    {
+        $clientFactory->createTokenAuthenticatedClient($user)->willReturn($client);
+        $this->create($user)
+            ->shouldReturnAnInstanceOf('DevBoardLib\GithubApiFacade\User\PaginatedKnpLabsUserFacade');
+    }
+}

@@ -0,0 +1,44 @@
+<?php
+
+namespace DevBoardLib\GithubApiFacade\User;
+
+use Github\Client;
+use Github\ResultPager;
+
+/**
+ * Class PaginatedKnpLabsUserFacade.
+ */
+class PaginatedKnpLabsUserFacade implements UserFacade
+{
+    /** @var  Client */
+    private $client;
+    /** @var ResultPager */
+    private $paginator;
+
+    /**
+     * PaginatedKnpLabsUserFacade constructor.
+     *
+     * @param Client $client
+     */
+    public function __construct(Client $client)
+    {
+        $this->client    = $client;
+        $this->paginator = new ResultPager($this->client);
+    }
+
+    /**
+     * @return array|mixed
+     */
+    public function fetchAllAccessibleRepos()
+    {
+        return $this->paginator->fetchAll($this->getMeApi(), 'repositories', ['type' => 'all']);
+    }
+
+    /**
+     * @return \Github\Api\ApiInterface
+     */
+    private function getMeApi()
+    {
+        return $this->client->api('me');
+    }
+}

@@ -0,0 +1,36 @@
+<?php
+
+namespace DevBoardLib\GithubApiFacade\User;
+
+use DevBoardLib\GithubApiFacade\Auth\GithubAccessToken;
+use DevBoardLib\GithubApiFacade\Client\ClientFactory;
+
+/**
+ * Class UserFacadeFactory.
+ */
+class UserFacadeFactory
+{
+    private $clientFactory;
+
+    /**
+     * RepoFacadeFactory constructor.
+     *
+     * @param $clientFactory
+     */
+    public function __construct(ClientFactory $clientFactory)
+    {
+        $this->clientFactory = $clientFactory;
+    }
+
+    /**
+     * @param GithubAccessToken $user
+     *
+     * @return PaginatedKnpLabsUserFacade
+     */
+    public function create(GithubAccessToken $user)
+    {
+        $client = $this->clientFactory->createTokenAuthenticatedClient($user);
+
+        return new PaginatedKnpLabsUserFacade($client);
+    }
+}

@@ -0,0 +1,54 @@
+<?php
+
+namespace tests\DevBoardLib\GithubApiFacade\User;
+
+use DevBoardLib\GithubApiFacade\Client\KnpLabsClientFactory;
+use DevBoardLib\GithubApiFacade\User\PaginatedKnpLabsUserFacade;
+use Mockery as m;
+
+/**
+ * Class PaginatedKnpLabsUserFacadeTest.
+ */
+class PaginatedKnpLabsUserFacadeTest extends \PHPUnit_Framework_TestCase
+{
+    private $facade;
+
+    public function setUp()
+    {
+        parent::setUp();
+
+        $this->facade = new PaginatedKnpLabsUserFacade(
+            $this->getTokenAuthenticatedApiClient()
+        );
+    }
+
+    /**
+     * @group GithubIntegration
+     * @group Live
+     */
+    public function testFetchAllAccessibleRepos()
+    {
+        self::assertCount(5, $this->facade->fetchAllAccessibleRepos());
+    }
+
+    /**
+     * @return \Github\Client
+     */
+    private function getTokenAuthenticatedApiClient()
+    {
+        $factory = new KnpLabsClientFactory();
+
+        return $factory->createTokenAuthenticatedClient($this->provideTestUser());
+    }
+
+    /**
+     * @return \DevBoardLib\GithubApiFacade\Auth\GithubAccessToken
+     */
+    private function provideTestUser()
+    {
+        $user = m::mock('DevBoardLib\GithubApiFacade\Auth\GithubAccessToken');
+        $user->shouldReceive('getGithubAccessToken')->andReturn(getenv('GITHUB_ACCESS_TOKEN'));
+
+        return $user;
+    }
+}

